const initialState={
    userInfo:{},
    loading:false
}
export const loginReducer=(state=initialState,action)=>{

switch(action.type){
  case "loginRequest":{
     return{
         ...state,
         userInfo:action.payload,
         loading:true
     }
  }
}
    
}
