const initialState={
    userInfo:{},
    loading:false
}
export const loginReducer=(state=initialState,action)=>{
    console.log('action in reducers',action)

switch(action.type){
  case "loginRequest":{
     return{
         ...state,
         userInfo:action.payload,
         loading:true
     }
  }
  default :{
      return state
  }
}
    
}
